                        html +='<h3>'+v.message+'</h3><div><p>'+v.address+'<br>电话:'+v.phone+'<br><a href="javascript:void(0)" style="color:#EE9A00;" jing_du="'+v.jing_du+'" wei_du="'+v.wei_du+'" message="'+v.message+'" phone="'+v.phone+'" address="'+v.address+'" class="all_map">查看完整地图</a></p></div> '; 
                    });                          
                    $('#accordion').html(html);          
                    $(".all_map").on('click',function(){        
                        var jing_du = $(this).attr("jing_du"); 
                        var wei_du = $(this).attr("wei_du");
                        var message = $(this).attr("message");
                        var phone = $(this).attr("phone");
                        var address = $(this).attr("address");        
                        $('#dialog-confirm').dialog({
                            resizable: false,
                            height:600,
                            width:800,
                            modal: true,
                            buttons: {
                                关闭: function() {
                                    $( this ).dialog( "close" );
                                    }
                            }
                        });
                         // 百度地图API功能
                        var map = new BMap.Map("dialog-confirm");    // 创建Map实例
                        map.enableScrollWheelZoom(true);     //开启鼠标滚轮缩放
                        var point = new BMap.Point(jing_du, wei_du);
                        var marker = new BMap.Marker(point);  // 创建标注
                        var content = '<span style="font-size:14px;color:#0A8021">'+address+'<br>电话:'+phone+'</span>';
                        map.addOverlay(marker);              // 将标注添加到地图中
                        map.centerAndZoom(point, 15);
                        var opts = {
                            width : 200,     // 信息窗口宽度
                            height: 100,     // 信息窗口高度
                            title : '<span style="font-size:14px;color:#0A8021">门店:'+message+'</span>', // 信息窗口标题
                            enableMessage:true,//设置允许信息窗发送短息
                        }
                        var infoWindow = new BMap.InfoWindow(content, opts);  // 创建信息窗口对象          
                        map.openInfoWindow(infoWindow,point); //开启信息窗口
                    });
                    //地图右侧信息
                    var map = new BMap.Map("allmap");       
                    var jing_du_value = new Array();
                    var wei_du_value =  new Array();
                    var message_value = new Array();
                    var i = 0;
                    var j = 0;
                    var m = 0;       
                    $(data).each(function(k,v){                    
                       jing_du_value[i++] =   v.jing_du; 
                       wei_du_value[j++]  =   v.wei_du; 
                       message_value[m++] =   v.message;                                    
                    });
                    var opts = {
                        width : 110,     // 信息窗口宽度
                        height: 60,     // 信息窗口高度
                        title : '<span style="font-size:14px;color:#0A8021">门店位置:</span>' , // 信息窗口标题
                        enableMessage:true//设置允许信息窗发送短息
                    };                 
                    for (var p = 0; p < m; p ++) {
                        var point = new BMap.Point(jing_du_value[p],wei_du_value[p]);
                        var content = '<span style="font-size:14px;color:#0A8021">'+message_value[p]+'</span>';
                        var marker = new BMap.Marker(point); 
                        map.addOverlay(marker); 
                        addClickHandler(content,marker);                     
                    }
                    function addClickHandler(content,marker){
                        marker.addEventListener("mouseover",function(e){
                            openInfo(content,e);
                        });
                        marker.addEventListener("mouseout",function(e){
                            map.closeInfoWindow();
                        });
                    }
                    function openInfo(content,e){
                        var p = e.target;
                        var point = new BMap.Point(p.getPosition().lng, p.getPosition().lat);
                        var infoWindow = new BMap.InfoWindow(content,opts);  // 创建信息窗口对象 
                        map.openInfoWindow(infoWindow,point); //开启信息窗口
                    }                    
                    map.centerAndZoom(point, 15);
                // 百度地图API功能
                    map.centerAndZoom(point,8); 
                    setTimeout(function(){
                          map.setZoom(14);   
                      }, 2000);  //2秒后放大到14级
                    map.enableScrollWheelZoom(true);
    //点击滑动转为放在上面滑动
                    $( "#accordion" ).accordion({
                      event: "click hoverintent"
                    });
                }
            });
        }
    });

</script>
<script>
  $.event.special.hoverintent = {
    setup: function() {
      $( this ).bind( "mouseover", jQuery.event.special.hoverintent.handler );
    },
    teardown: function() {
      $( this ).unbind( "mouseover", jQuery.event.special.hoverintent.handler );
    },
    handler: function( event ) {
      var currentX, currentY, timeout,
        args = arguments,
        target = $( event.target ),
        previousX = event.pageX,
        previousY = event.pageY;
 
      function track( event ) {
        currentX = event.pageX;
        currentY = event.pageY;
      };
 
      function clear() {
        target
          .unbind( "mousemove", track )
          .unbind( "mouseout", clear );
        clearTimeout( timeout );
      }
 
      function handler() {
        var prop,
          orig = event;
 
        if ( ( Math.abs( previousX - currentX ) +
            Math.abs( previousY - currentY ) ) < 7 ) {
          clear();
 
          event = $.Event( "hoverintent" );
          for ( prop in orig ) {
            if ( !( prop in event ) ) {
              event[ prop ] = orig[ prop ];
            }
          }
          // 防止访问原始事件，因为新事件会被异步触发，旧事件不再可用 (#6028)
          delete event.originalEvent; 
          target.trigger( event );
        } else {
          previousX = currentX;
          previousY = currentY;
          timeout = setTimeout( handler, 100 );
        }
      }
 
      timeout = setTimeout( handler, 100 );
      target.bind({
        mousemove: track,
        mouseout: clear
      });
    }
  };
</script>